mapper.select:
select 

from ACCOUNT_TRUST
where ACCOUNT_NO = #{accountNo,jdbcType=VARCHAR}
  and TRUST_MGR_CODE = #{trustMgrCode,jdbcType=VARCHAR}

mapper.delete:
delete from ACCOUNT_TRUST
where ACCOUNT_NO = #{accountNo,jdbcType=VARCHAR}
  and TRUST_MGR_CODE = #{trustMgrCode,jdbcType=VARCHAR}

mapper.update:
update ACCOUNT_TRUST
set TRUST_MGR_NAME = #{trustMgrName,jdbcType=VARCHAR},
  MERCHANT_NO = #{merchantNo,jdbcType=VARCHAR},
  USE_TYPE = #{useType,jdbcType=DECIMAL},
  CREATE_TIME = #{createTime,jdbcType=TIMESTAMP},
  UPDATE_TIME = #{updateTime,jdbcType=TIMESTAMP}
where ACCOUNT_NO = #{accountNo,jdbcType=VARCHAR}
  and TRUST_MGR_CODE = #{trustMgrCode,jdbcType=VARCHAR}
